import React, { useState } from 'react';
import axios from 'axios';
import { Search, Loader2, AlertCircle, CheckCircle, Info } from 'lucide-react';
import './CipherForm.css';

function CipherForm() {
  const [ciphertext, setCiphertext] = useState('');
  const [result, setResult] = useState(null);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState('');
  const [algorithms, setAlgorithms] = useState([]);

  // Fetch supported algorithms on component mount
  React.useEffect(() => {
    fetchAlgorithms();
  }, []);

  const fetchAlgorithms = async () => {
    try {
      const response = await axios.get('/api/crypto/algorithms');
      setAlgorithms(response.data.algorithms || []);
    } catch (err) {
      console.error('Failed to fetch algorithms:', err);
    }
  };

  const handleSubmit = async (e) => {
    e.preventDefault();

    if (!ciphertext.trim()) {
      setError('Please enter some ciphertext to analyze');
      return;
    }

    setLoading(true);
    setError('');
    setResult(null);

    try {
      const response = await axios.post('/api/crypto/guess', { 
        ciphertext: ciphertext.trim() 
      });

      setResult(response.data);
    } catch (err) {
      console.error('Prediction error:', err);
      setError(err.response?.data?.error || 'Failed to predict algorithm. Please try again.');
    } finally {
      setLoading(false);
    }
  };

  const handleClear = () => {
    setCiphertext('');
    setResult(null);
    setError('');
  };

  const getAlgorithmColor = (algorithm) => {
    const colors = {
      'Caesar Cipher': '#FF6B6B',
      'VigenÃ¨re Cipher': '#4ECDC4',
      'Substitution Cipher': '#45B7D1',
      'Transposition Cipher': '#96CEB4',
      'Modern Encryption': '#FFEAA7'
    };
    return colors[algorithm] || '#6C5CE7';
  };

  return (
    <div className="cipher-form-container">
      <div className="form-card">
        <form onSubmit={handleSubmit}>
          <div className="input-group">
            <label htmlFor="ciphertext">
              <Search size={20} />
              Enter Ciphertext
            </label>
            <textarea
              id="ciphertext"
              value={ciphertext}
              onChange={(e) => setCiphertext(e.target.value)}
              placeholder="Paste your encrypted text here... (e.g., 'KHOOR ZRUOG' for Caesar cipher)"
              rows="6"
              disabled={loading}
            />
          </div>

          <div className="button-group">
            <button 
              type="submit" 
              disabled={loading || !ciphertext.trim()}
              className="submit-btn"
            >
              {loading ? (
                <>
                  <Loader2 size={18} className="spin" />
                  Analyzing...
                </>
              ) : (
                <>
                  <Search size={18} />
                  Predict Algorithm
                </>
              )}
            </button>

            <button 
              type="button" 
              onClick={handleClear}
              className="clear-btn"
              disabled={loading}
            >
              Clear
            </button>
          </div>
        </form>

        {/* Error Display */}
        {error && (
          <div className="error-message">
            <AlertCircle size={18} />
            {error}
          </div>
        )}

        {/* Result Display */}
        {result && (
          <div className="result-card">
            <div className="result-header">
              <CheckCircle size={24} color="#00D4AA" />
              <h3>Prediction Result</h3>
            </div>

            <div 
              className="algorithm-badge"
              style={{ backgroundColor: getAlgorithmColor(result.algorithm) }}
            >
              {result.algorithm}
            </div>

            <div className="confidence-bar">
              <span>Confidence: {Math.round(result.confidence * 100)}%</span>
              <div className="confidence-progress">
                <div 
                  className="confidence-fill"
                  style={{ width: `${result.confidence * 100}%` }}
                />
              </div>
            </div>

            {result.features && (
              <div className="features-grid">
                <div className="feature">
                  <span className="feature-label">Length:</span>
                  <span className="feature-value">{result.features.length}</span>
                </div>
                <div className="feature">
                  <span className="feature-label">Entropy:</span>
                  <span className="feature-value">{result.features.entropy}</span>
                </div>
                <div className="feature">
                  <span className="feature-label">Diversity:</span>
                  <span className="feature-value">{result.features.diversity}</span>
                </div>
                <div className="feature">
                  <span className="feature-label">Pattern Score:</span>
                  <span className="feature-value">{result.features.pattern_score}</span>
                </div>
              </div>
            )}
          </div>
        )}

        {/* Supported Algorithms */}
        {algorithms.length > 0 && (
          <div className="algorithms-info">
            <div className="info-header">
              <Info size={18} />
              <span>Supported Algorithms</span>
            </div>
            <div className="algorithms-list">
              {algorithms.map((algo, index) => (
                <span 
                  key={index} 
                  className="algorithm-tag"
                  style={{ backgroundColor: getAlgorithmColor(algo) }}
                >
                  {algo}
                </span>
              ))}
            </div>
          </div>
        )}
      </div>
    </div>
  );
}

export default CipherForm;
